import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

const PromptInterface = () => {
  const [email, setEmail] = useState('');
  const navigate = useNavigate(); // Initialize useNavigate

  // Refs for chat messages, title, and footer title
  const userMessageRef = useRef(null);
  const aiMessageRef = useRef(null);
  const userMessageRef2 = useRef(null);
  const aiMessageRef2 = useRef(null);
  const titleRef = useRef(null); // Ref for the main title
  const footerTitleRef = useRef(null); // Ref for the footer title

  // Intersection Observer to trigger animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('fade-in');
            entry.target.classList.remove('fade-out');
          } else {
            entry.target.classList.add('fade-out');
            entry.target.classList.remove('fade-in');
          }
        });
      },
      {
        threshold: 0.5, // Trigger when 50% of the element is visible
      }
    );

    if (userMessageRef.current) observer.observe(userMessageRef.current);
    if (aiMessageRef.current) observer.observe(aiMessageRef.current);
    if (userMessageRef2.current) observer.observe(userMessageRef2.current);
    if (aiMessageRef2.current) observer.observe(aiMessageRef2.current);

    return () => {
      if (userMessageRef.current) observer.unobserve(userMessageRef.current);
      if (aiMessageRef.current) observer.unobserve(aiMessageRef.current);
      if (userMessageRef2.current) observer.unobserve(userMessageRef2.current);
      if (aiMessageRef2.current) observer.unobserve(aiMessageRef2.current);
    };
  }, []);

  // Parallax and fade effect for the title
  useEffect(() => {
    const handleScroll = () => {
      if (titleRef.current && footerTitleRef.current) {
        const scrollY = window.scrollY;
        const windowHeight = window.innerHeight;
        const documentHeight = document.documentElement.scrollHeight;

        // Parallax and fade for the main title
        const parallaxOffset = scrollY * 0.5; // Adjust the parallax speed
        const opacity = 1 - scrollY / 200; // Adjust the fade speed
        titleRef.current.style.transform = `translateY(${parallaxOffset}px)`;
        titleRef.current.style.opacity = opacity > 0 ? opacity : 0;

        // Show footer title when scrolled to the bottom
        if (scrollY + windowHeight >= documentHeight - 100) {
          footerTitleRef.current.style.opacity = 1;
          footerTitleRef.current.style.transform = 'translateY(0)';
        } else {
          footerTitleRef.current.style.opacity = 0;
          footerTitleRef.current.style.transform = 'translateY(20px)';
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="min-h-screen bg-black relative overflow-y-auto"> {/* Make the page scrollable */}
      {/* Background Grid (Animated) */}
      <div 
        className="fixed inset-0 z-0" // Fixed position and z-index to keep it behind content
        style={{
          backgroundImage: `
            linear-gradient(to right, rgba(255,255,255,0.05) 1px, transparent 1px),
            linear-gradient(to bottom, rgba(255,255,255,0.05) 1px, transparent 1px)
          `,
          backgroundSize: '100px 100px',
          animation: 'moveGrid 10s linear infinite',
        }}
      />

      {/* Animated Gradient Orbs */}
      <style>
        {`
          @keyframes moveGradient1 {
            0% { transform: translate(0, 0) scale(1); }
            33% { transform: translate(100px, 50px) scale(1.2); }
            66% { transform: translate(-50px, 100px) scale(0.8); }
            100% { transform: translate(0, 0) scale(1); }
          }
          @keyframes moveGradient2 {
            0% { transform: translate(0, 0) scale(1); }
            33% { transform: translate(-100px, -50px) scale(0.8); }
            66% { transform: translate(50px, -100px) scale(1.2); }
            100% { transform: translate(0, 0) scale(1); }
          }
          @keyframes moveGradient3 {
            0% { transform: translate(-50%, -50%) scale(1); }
            33% { transform: translate(-50%, -50%) scale(1.2); }
            66% { transform: translate(-50%, -50%) scale(0.8); }
            100% { transform: translate(-50%, -50%) scale(1); }
          }
          .gradient-orb-1 {
            animation: moveGradient1 15s infinite ease-in-out;
          }
          .gradient-orb-2 {
            animation: moveGradient2 18s infinite ease-in-out;
          }
          .gradient-orb-3 {
            animation: moveGradient3 20s infinite ease-in-out;
          }

          /* Fade-in Animation */
          @keyframes fadeIn {
            0% { opacity: 0; transform: translateY(20px); }
            100% { opacity: 1; transform: translateY(0); }
          }
          .fade-in {
            opacity: 1;
            transform: translateY(0);
            transition: opacity 0.5s ease-in-out, transform 0.5s ease-in-out;
          }

          /* Fade-out Animation */
          @keyframes fadeOut {
            0% { opacity: 1; transform: translateY(0); }
            100% { opacity: 0; transform: translateY(20px); }
          }
          .fade-out {
            opacity: 0;
            transform: translateY(20px);
            transition: opacity 0.5s ease-in-out, transform 0.5s ease-in-out;
          }

          /* Gradient Glassmorphism Effect */
          .gradient-glass {
            background: linear-gradient(145deg, rgba(255, 255, 255, 0.15), rgba(255, 255, 255, 0.05));
            backdrop-filter: blur(10px);
            border: 1px solid rgba(255, 255, 255, 0.1);
            border-radius: 12px;
            transition: all 0.3s ease-in-out;
            box-shadow: 0 2px 10px rgba(255, 255, 255, 0.05); /* Reduced glow */
          }
          .gradient-glass:hover {
            background: linear-gradient(145deg, rgba(255, 255, 255, 0.2), rgba(255, 255, 255, 0.1));
            box-shadow: 0 4px 20px rgba(255, 255, 255, 0.1); /* Reduced glow on hover */
          }

          /* Grid Animation */
          @keyframes moveGrid {
            0% { background-position: 0 0; }
            100% { background-position: 100px 100px; }
          }
        `}
      </style>
      
      <div className="absolute top-0 right-0 w-96 h-96 bg-purple-500 rounded-full opacity-10 blur-3xl gradient-orb-1" />
      <div className="absolute bottom-0 left-0 w-96 h-96 bg-teal-400 rounded-full opacity-10 blur-3xl gradient-orb-2" />
      <div className="absolute top-1/2 left-1/2 w-96 h-96 bg-pink-200 rounded-full opacity-10 blur-3xl gradient-orb-3" />

      {/* Social Links */}
      <div className="fixed left-8 bottom-8 space-y-4 z-10">
        <div className="text-white opacity-50 hover:opacity-100 cursor-pointer transition-opacity">ùïè</div>
        <div className="text-white opacity-50 hover:opacity-100 cursor-pointer transition-opacity">f</div>
        <div className="text-white opacity-50 hover:opacity-100 cursor-pointer transition-opacity">üì∏</div>
      </div>

      {/* Navigation Dots */}
      <div className="fixed right-8 top-1/2 -translate-y-1/2 space-y-6 z-10">
        <div className="w-3 h-3 rounded-full bg-pink-200 cursor-pointer" />
        <div className="w-3 h-3 rounded-full bg-purple-500 cursor-pointer" />
        <div className="w-3 h-3 rounded-full bg-teal-400 cursor-pointer" />
      </div>

      {/* Main Content */}
      <div className="relative z-10 max-w-4xl mx-auto px-4 py-20 flex flex-col items-center"> {/* Centered content */}
        <div className="w-full max-w-2xl space-y-12"> {/* Reduced vertical spacing */}
          {/* Header Section */}
          <div className="text-center space-y-6">
            {/* Logo Icon */}
            <div className="inline-block mb-2">
              <div className="w-8 h-8 rounded-lg bg-pink-200 bg-opacity-20 flex items-center justify-center">
                <div className="w-4 h-4 rounded-sm bg-pink-200" />
              </div>
            </div>
            
            {/* Title with Parallax Effect */}
            <h1
              ref={titleRef}
              className="text-5xl font-bold text-white leading-tight transition-transform duration-300"
            >
              <br />AI Advisor
            </h1>
            
            {/* Description */}
            <p className="text-gray-400 text-sm max-w-lg mx-auto">
              These are just a few of the many attractions Paris has to offer. Let me know if you'd like more information or details on anything specific!
            </p>
          </div>

          {/* Prompt Input Area */}
          <div className="gradient-glass p-3 border border-gray-800">
            <div className="flex gap-3 items-center">
              <input
                type="text"
                placeholder="Write a message"
                className="bg-transparent text-white px-4 py-3 flex-1 focus:outline-none text-sm font-medium"
              />
              <button
                className="bg-pink-200 text-black px-6 py-2 rounded-xl font-medium hover:bg-pink-300 transition-colors"
                onClick={() => navigate('/register')}
              >
                Get Started
              </button>
            </div>
          </div>

          {/* Conversation Section */}
          <div className="space-y-12"> {/* Reduced vertical spacing */}
            {/* User Message */}
            <div className="flex justify-end" ref={userMessageRef}>
              <div className="flex items-end gap-6 max-w-[70%] ml-auto"> {/* Increased horizontal spacing */}
                <div className="gradient-glass p-4 rounded-2xl rounded-br-none">
                  <p className="text-white font-medium">Hey Kalp, I heard this AI can also help with creative tasks like writing stories, generating ideas, and even composing music. Is that true?</p>
                </div>
                <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
                  <span className="text-white text-sm">üë§</span> {/* User avatar */}
                </div>
              </div>
            </div>

            {/* AI Message */}
            <div className="flex justify-start" ref={aiMessageRef}>
              <div className="flex items-end gap-6 max-w-[70%] mr-auto"> {/* Increased horizontal spacing */}
                <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
                  <span className="text-white text-sm">ü§ñ</span> {/* AI avatar */}
                </div>
                <div className="gradient-glass p-4 rounded-2xl rounded-bl-none">
                  <p className="text-white font-medium">Absolutely! This AI is equipped with creative capabilities. Whether you need a story idea, a poem, or even a melody, it can generate unique and creative content tailored to your needs.</p>
                </div>
              </div>
            </div>

            {/* User Message */}
            <div className="flex justify-end" ref={userMessageRef2}>
              <div className="flex items-end gap-6 max-w-[70%] ml-auto"> {/* Increased horizontal spacing */}
                <div className="gradient-glass p-4 rounded-2xl rounded-br-none">
                  <p className="text-white font-medium">That's amazing! Can you give me an example of a story idea?</p>
                </div>
                <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
                  <span className="text-white text-sm">üë§</span> {/* User avatar */}
                </div>
              </div>
            </div>

            {/* AI Message */}
            <div className="flex justify-start" ref={aiMessageRef2}>
              <div className="flex items-end gap-6 max-w-[70%] mr-auto"> {/* Increased horizontal spacing */}
                <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
                  <span className="text-white text-sm">ü§ñ</span> {/* AI avatar */}
                </div>
                <div className="gradient-glass p-4 rounded-2xl rounded-bl-none">
                  <p className="text-white font-medium">Sure! How about this: A young explorer discovers a hidden city in the Amazon rainforest, but the city is guarded by ancient AI-powered robots. The story could explore themes of technology, nature, and humanity.</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer Title */}
      <div
        ref={footerTitleRef}
        className="fixed bottom-0 left-0 w-full text-center py-8 opacity-0 transform translate-y-20 transition-all duration-500"
        style={{ marginBottom: '100px' }} 
        
      >
        <h1 className="text-5xl font-bold text-white leading-tight">
          Your Personal<br />AI Advisor
        </h1>
      </div>

      {/* Extra Space at the Bottom */}
      <div className="h-48"></div> {/* Add extra space to increase scrollability */}
    </div>
  );
};

export default PromptInterface;
